// src/lib/plugins.ts
var LOYALTY_PLUGIN_NAME = "@medusajs/loyalty-plugin";
var getLoyaltyPlugin = (plugins) => {
  return plugins?.find((plugin) => plugin.name === LOYALTY_PLUGIN_NAME);
};

// src/lib/orders.ts
var getPaymentsFromOrder = (order) => {
  return order.payment_collections.map((collection) => collection.payments).flat(1).filter(Boolean);
};

export {
  getPaymentsFromOrder,
  getLoyaltyPlugin
};
